import './index.css';
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Sidebar } from './components/Sidebar';
import { Dashboard } from './components/Dashboard';
import { Layout } from './components/Layout';
import { Home } from './components/Home/Home';
import { HazardReportForm } from './components/Dashboard/HazardRiskManagements';
import { RiskAssessmentForm}  from './components/Dashboard/HealthAndSafety';
import IncidentInvestigationForm from './components/Dashboard/IncidentsInvestigationForm';
import { AboutUs } from './components/Pages/AboutUs';
import { Contacts } from './components/Pages/Contacts';
import { Products } from './components/Pages/Products';
import { Solutions } from './components/Pages/Solutions';
import { PublicLayout } from './components/PublicLayout';
import  HazardDashboard  from './components/Dashboard/HazardDashboard';


export default function App() {
  const [sidebarToggle, setSidebarToggle] = useState(false);

  return (
    <Router>
      <Routes>
        {/* Home page with no sidebar */}

        <Route
  path="/"
  element={
    <PublicLayout>
      <Home />
    </PublicLayout>
  }
/>




        {/* Dashboard page with sidebar */}
        <Route
          path="/dashboard"
          element={
            <div className="flex">
              <Sidebar sidebarToggle={sidebarToggle} />
              <Dashboard
                sidebarToggle={sidebarToggle}
                setSidebarToggle={setSidebarToggle}
              />
            </div>
          }
        />


<Route
  path="/hazard"
  element={
    <Layout sidebarToggle={sidebarToggle} setSidebarToggle={setSidebarToggle}>
      <HazardDashboard />
    </Layout>
  }
/>



<Route
  path="/hazard"
  element={
    <Layout
      sidebarToggle={sidebarToggle}
      setSidebarToggle={setSidebarToggle}
    >
      <HazardReportForm />
    </Layout>
  }
/>

<Route
  path="/health-and-safety"
  element={
    <Layout
      sidebarToggle={sidebarToggle}
      setSidebarToggle={setSidebarToggle}
    >
      <RiskAssessmentForm />
    </Layout>
  }
/>

<Route
  path="/incident-management"
  element={
    <Layout
      sidebarToggle={sidebarToggle}
      setSidebarToggle={setSidebarToggle}
    >
      <IncidentInvestigationForm />
    </Layout>
  }
/>



<Route
  path="/aboutus"
  element={
    <PublicLayout>
      <AboutUs />
    </PublicLayout>
  }
/>

<Route
  path="/products"
  element={
    <PublicLayout>
      <Products />
    </PublicLayout>
  }
/>

<Route
  path="/solutions"
  element={
    <PublicLayout>
      <Solutions />
    </PublicLayout>
  }
/>

<Route
  path="/contacts"
  element={
    <PublicLayout>
      <Contacts />
    </PublicLayout>
  }
/>

      </Routes>
    </Router>
  );
}
