import React, { useState } from "react";
import { HazardReportForm } from "./HazardRiskManagements";


export function HazardDashboard() {
  const [showForm, setShowForm] = useState(false);
  const [reports, setReports] = useState([]);

  const handleAddReport = (data) => {
    setReports(prev => [...prev, data]);
    setShowForm(false); // close modal after submit
  };

  return (
    <div className="max-w-6xl mx-auto p-6 bg-gray-100 min-h-screen text-center">
      <div className="flex justify-between items-center mb-6">
                <h1 className="text-2xl font-bold text-gray-800 ">Hazard-table</h1>

        <button
          onClick={() => setShowForm(true)}
          className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700"
        >
          Create Report
        </button>
      </div>

      {/* Report Table */}
      <div className="overflow-x-auto bg-white shadow rounded">
        <table className="min-w-full table-auto text-sm">
          <thead className="bg-indigo-100 text-left">
            <tr>
              <th className="px-4 py-2">Date</th>
              <th className="px-4 py-2">Location</th>
              <th className="px-4 py-2">Hazard</th>
              <th className="px-4 py-2">Hurt?</th>
              <th className="px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {reports.map((report, idx) => (
              <tr key={idx} className="border-t">
                <td className="px-4 py-2">{report.date}</td>
                <td className="px-4 py-2">{report.location}</td>
                <td className="px-4 py-2">{report.hazard}</td>
                <td className="px-4 py-2">{report.hurt}</td>
                <td className="px-4 py-2 text-indigo-600">View</td>
              </tr>
            ))}
            {reports.length === 0 && (
              <tr>
                <td colSpan="5" className="text-center px-4 py-6 text-gray-500">
                  No reports submitted yet.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Popup Form */}
      {showForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-md shadow-lg p-6 w-full max-w-2xl relative">
            <button
              className="absolute top-2 right-2 text-gray-500 hover:text-black"
              onClick={() => setShowForm(false)}
            >
              ×
            </button>
            <HazardReportForm
              onSubmit={(data) => handleAddReport(data)}
            />
          </div>
        </div>
      )}
    </div>
  );
}
