import React, { useState } from "react";
import { HazardReportForm } from "./HazardRiskManagements";
import { FaPlus } from "react-icons/fa";


export function HazardDashboard() {
  const [showForm, setShowForm] = useState(false);
  const [reports, setReports] = useState([]);

  const handleAddReport = (data) => {
    setReports(prev => [...prev, data]);
    setShowForm(false); // close modal after submit
  };

  return (
    <div className="max-w-6xl mx-auto p-6 bg-gray-100 min-h-screen text-center">
      <div className="flex justify-between items-center mb-6">
                <h1 className="text-3xl font-bold text-gray-800  ">Hazard-table</h1>
                <div className="flex justify-end mb-8">

        <button
          onClick={() => setShowForm(true)}
          className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700"
        >
        <FaPlus className="text-white text-base relative top-[1px]" />

           
        </button>
        </div>
      </div>


      <div className="overflow-x-auto bg-white shadow-lg rounded-lg border border-gray-200">
  <table className="min-w-full text-sm text-gray-800">
    <thead className="bg-gray-100 text-gray-700 uppercase text-xs tracking-wider">
      <tr>
        <th className="px-6 py-3 text-left">Date</th>
        <th className="px-6 py-3 text-left">Location</th>
        <th className="px-6 py-3 text-left">Hazard</th>
        <th className="px-6 py-3 text-left">Hurt?</th>
        <th className="px-6 py-3 text-left">Actions</th>
      </tr>
    </thead>
    <tbody className="divide-y divide-gray-200">
      {reports.map((report, idx) => (
        <tr key={idx} className="hover:bg-gray-50 transition">
          <td className="px-6 py-3">{report.date}</td>
          <td className="px-6 py-3">{report.location}</td>
          <td className="px-6 py-3">{report.hazard}</td>
          <td className="px-6 py-3">{report.hurt}</td>
          <td className="px-6 py-3">
            <button className="text-indigo-600 hover:text-indigo-800 font-semibold">
              View
            </button>
          </td>
        </tr>
      ))}
      {reports.length === 0 && (
        <tr>
          <td colSpan="5" className="text-center px-6 py-10 text-gray-400 italic">
            No reports submitted yet.
          </td>
        </tr>
      )}
    </tbody>
  </table>
</div>










      {showForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-md shadow-xl p-8 w-full max-w-2xl border border-gray-200 relative">
            <button
              className="absolute top-4 right-4 text-gray-500 hover:text-gray-700 text-2xl font-bold"
              onClick={() => setShowForm(false)}
            >
              ×
            </button>
            <HazardReportForm
              onSubmit={(data) => handleAddReport(data)}
            />
          </div>
        </div>
      )}
    </div>
  );
}
