import React, { useState } from "react";

const sections = [
  "General Information",
  "Kitchen Area",
  "Serving Area",
  "Eating Area",
  "Personnel & Procedures",
  "Issues & Action Plan",
  "Sign-Off"
];

export default function CanteenFormModal() {
  const [isOpen, setIsOpen] = useState(false);
  const [currentSection, setCurrentSection] = useState(0);

  const nextSection = () => {
    if (currentSection < sections.length - 1) {
      setCurrentSection(currentSection + 1);
    }
  };

  const prevSection = () => {
    if (currentSection > 0) {
      setCurrentSection(currentSection - 1);
    }
  };

  const closeModal = () => {
    setIsOpen(false);
    setCurrentSection(0);
  };

  return (
    <div>
      <button
        onClick={() => setIsOpen(true)}
        className="px-4 py-2 bg-primary text-tertiary rounded hover:bg-green-700"
      >
        + Launch Canteen Form
      </button>

<div className="flex items-center gap-4 mt-4">
  

  <div className="flex gap-4">
    {sections.map((label, index) => (
      <button
        key={index}
        onClick={() => {
          setIsOpen(true);
          setCurrentSection(index);
        }}
        className={`flex flex-col items-center px-2 py-1 transition ${
          currentSection === index && isOpen
            ? "text-blue-600 font-semibold"
            : "text-gray-500 hover:text-blue-500"
        }`}
      >
        <div
          className={`w-8 h-8 flex items-center justify-center rounded-full border ${
            currentSection === index && isOpen
              ? "border-blue-600 bg-primary"
              : "border-gray-300"
          }`}
        >
          {index + 1}
        </div>
        <span className="text-s mt-1">{label}</span>
      </button>
    ))}
  </div>
</div>



      {isOpen && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-40 backdrop-blur-sm">
          <div className="bg-white rounded-lg shadow-xl max-w-4xl w-full max-h-[90vh] overflow-y-auto relative p-6">
            {/* Close Button */}
            <button
              onClick={closeModal}
              className="absolute top-3 right-4 text-gray-500 hover:text-gray-800 text-2xl font-bold"
            >
              &times;
            </button>

            {/* Header */}
            <h1 className="text-2xl font-bold text-center text-green-700 mb-2">
              CANTEEN INSPECTION FORM
            </h1>
            <p className="text-sm text-gray-500 text-center mb-6">
              CONFIDENTIAL COMPLIANCE DOCUMENT
            </p>

            

            {/* Section Content */}
            <div className="mb-6">
              <h2 className="text-xl font-semibold mb-4">
                SECTION {currentSection + 1}: {sections[currentSection]}
              </h2>
              <div className="space-y-4">
                {currentSection === 0 && (
                  <div className="grid grid-cols-2 gap-4">
                    <input placeholder="Inspector Name" className="border p-2 rounded" />
                    <input type="date" className="border p-2 rounded" />
                    <input placeholder="Canteen Location" className="border p-2 rounded" />
                    <input placeholder="Supervisor Name" className="border p-2 rounded" />
                  </div>
                )}

                {currentSection === 1 && (
                  <ChecklistSection
                    items={[
                      "Food preparation surfaces clean and sanitized",
                      "Handwashing facilities available and functional",
                      "Food stored at appropriate temperatures",
                      "Perishables stored off the floor and covered",
                      "Waste bins covered and emptied regularly",
                      "Pest control measures in place and effective",
                      "Refrigerators and freezers functioning properly",
                      "Cooking equipment clean and maintained",
                      "Cleaning supplies stored separately",
                      "Floor clean, dry, and slip-resistant",
                      "No signs of pests"
                    ]}
                  />
                )}

                {currentSection === 2 && (
                  <ChecklistSection
                    items={[
                      "Serving counters clean and clutter-free",
                      "Food handlers wear gloves/hairnets/aprons",
                      "Hot food held at proper temperature",
                      "Cold food kept refrigerated or on ice",
                      "Utensils and serving equipment clean",
                      "Displayed food covered and protected",
                      "Allergen information visibly posted"
                    ]}
                  />
                )}

                {currentSection === 3 && (
                  <ChecklistSection
                    items={[
                      "Tables and chairs clean and undamaged",
                      "Waste bins available and emptied frequently",
                      "Floors swept/mopped regularly",
                      "Adequate ventilation and lighting",
                      "No signs of pests",
                      "Hand sanitizer or wash station nearby"
                    ]}
                  />
                )}

                {currentSection === 4 && (
                  <ChecklistSection
                    items={[
                      "Canteen staff trained in food safety",
                      "Staff wear clean uniforms/PPE",
                      "Cleaning schedules followed and documented",
                      "First aid kit available in canteen",
                      "Emergency numbers posted in visible area"
                    ]}
                  />
                )}

                {currentSection === 5 && (
                  <div className="space-y-2">
                    {[1, 2, 3].map((i) => (
                      <div key={i} className="grid grid-cols-4 gap-4">
                        <input placeholder="Issue" className="border p-2 rounded" />
                        <input placeholder="Corrective Action" className="border p-2 rounded" />
                        <input placeholder="Person Responsible" className="border p-2 rounded" />
                        <input type="date" className="border p-2 rounded" />
                      </div>
                    ))}
                  </div>
                )}



                {currentSection === 6 && (
  <div className="space-y-8">

    {/* Inspectors Section */}
    <div className="grid grid-cols-2 gap-8 bg-gray-50 p-6 rounded-lg shadow-sm">
      <div className="space-y-4">
        <label className="block text-sm font-medium text-gray-600">Inspectors</label>
        <div className="flex gap-4">
          {['Paul Amegah', 'Debby', 'Eric'].map((name, idx) => (
            <div key={idx} className="border rounded px-4 py-2 text-gray-700 bg-white shadow-sm">
              {name}
            </div>
          ))}
        </div>

        <label className="block text-sm font-medium text-gray-600">sign</label>
        <div className="border rounded px-4 py-2 text-blue-600 italic shadow-sm">
          Paul Amegah
        </div>

        <label className="block text-sm font-medium text-gray-600">Date</label>
        <div className="border rounded px-4 py-2 text-gray-700 shadow-sm">
          28th July 2025
        </div>
      </div>

      {/* Canteen Committee Chair Section */}
      <div className="space-y-4">
        <label className="block text-sm font-medium text-gray-600">Canteen Committee Chair</label>
        <div className="border rounded px-4 py-2 text-gray-700 bg-white shadow-sm">
          Paul
        </div>

        <label className="block text-sm font-medium text-gray-600">Signature</label>
        <div className="border-2 border-dashed border-blue-300 rounded-lg p-6 flex flex-col items-center justify-center text-gray-500">
          <svg className="w-8 h-8 text-blue-400 mb-2" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" d="M4 16v1a2 2 0 002 2h12a2 2 0 002-2v-1M4 12l8-8 8 8M12 4v12" />
          </svg>
          <span>Drag and drop your file or</span>
          <button className="mt-2 px-4 py-1 bg-blue-500 text-white rounded hover:bg-blue-600">Browse File</button>
        </div>

        <label className="block text-sm font-medium text-gray-600">Date</label>
        <input type="date" className="border rounded px-4 py-2 text-gray-700 shadow-sm w-full" />
      </div>
    </div>

    
  </div>
)}
              </div>
            </div>
















            {/* Navigation Buttons */}
            <div className="flex justify-between mt-6">
              <button
                onClick={prevSection}
                disabled={currentSection === 0}
                className={`px-4 py-2 rounded ${
                  currentSection === 0
                    ? "bg-primary text-tertiary cursor-not-allowed"
                    : "bg-primary text-tertiary hover:bg-gray-700"
                }`}
              >
                Previous
              </button>
              {currentSection < sections.length - 1 ? (
                <button
                  onClick={nextSection}
                  className="px-4 py-2 bg-primary text-tertiary rounded hover:bg-green-700"
                >
                  Next
                </button>
              ) : (
                <button
                  onClick={() => alert("Form submitted!")}
                  className="px-6 py-2 bg-primary text-tertiary rounded hover:bg-blue-700"
                >
                  Submit
                </button>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

const ChecklistSection = ({ items }) => (
  <table className="w-full border border-gray-300 table-auto">
    <thead className="bg-gray-100">
      <tr>
        <th className="px-4 py-2 text-left">Item</th>
        <th className="px-4 py-2 text-center">√ / ⤫ / N/A</th>
        <th className="px-4 py-2 text-left">Comments</th>
      </tr>
    </thead>
    <tbody className="divide-y divide-gray-200">
      {items.map((item, i) => (
        <tr key={i}>
          <td className="px-4 py-2">{item}</td>
          <td className="px-4 py-2 text-center">
            <select className="border rounded px-2 py-1">
              <option value="√">√</option>
              <option value="⤫">⤫</option>
              <option value="N/A">N/A</option>
            </select>
          </td>
          <td className="px-4 py-2">
            <input
              type="text"
              placeholder="Enter comment"
               className="w-full border rounded px-2 py-1" />
                </td>
                 </tr> 
                 ))}
                  </tbody>
                   </table> 
                   );
