  import React, { useState } from "react";

export  function HazardReportForm() {
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    date: "",
    name: "",
    location: "",
    hazard: "",
    hurt: "",
    hurtDetails: "",
    actionTaken: "",
  });

  const hazardOptions = [
    "Slippery floor or wet surface",
    "Broken furniture or equipment",
    "Loose or damaged electric wires",
    "Poor lighting",
    "Blocked exit or fire escape",
    "Playground equipment issue",
    "Trip hazard (e.g. uneven floor, cables)",
    "Dangerous chemicals or substances",
    "Unsafe behavior (e.g. running in halls, bullying)",
    "Environmental issue",
    "Other",
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form Submitted:", formData);
    setShowForm(false); // close modal after submit
  };

  return (
    <div className="relative">
      {/* Hazard Table Section */}
      <div className={showForm ? "opacity-30 pointer-events-none transition duration-300" : "opacity-100"}>
        <h1 className="text-xl font-bold mb-4">Hazard Table</h1>
        <p>No reports submitted yet.</p>
      </div>

      {/* Add Button */}
      <button
        onClick={() => setShowForm(true)}
        className="mt-6 px-4 py-2 bg-indigo-600 text-white rounded shadow hover:bg-indigo-700"
      >
        + Add Hazard Report
      </button>

      {/* Form Modal */}
      {showForm && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-40 backdrop-blur-sm">
          <div className="bg-white p-6 rounded-lg shadow-lg max-w-2xl w-full relative space-y-4">
            {/* Close Button */}
            <button
              onClick={() => setShowForm(false)}
              className="absolute top-2 right-2 text-gray-500 hover:text-gray-700 text-xl"
            >
              ✕
            </button>

            <h2 className="text-2xl font-bold text-indigo-600 mb-2 text-center">Hazard Report Form</h2>

            <form onSubmit={handleSubmit}>
              <label className="block">
                <span className="text-gray-700">Date of Report:</span>
                <input type="date" name="date" value={formData.date} onChange={handleChange}
                  className="mt-1 block w-full border border-gray-300 rounded-md p-2" />
              </label>

              <label className="block">
                <span className="text-gray-700">Name (optional):</span>
                <input type="text" name="name" value={formData.name} onChange={handleChange}
                  className="mt-1 block w-full border border-gray-300 rounded-md p-2" />
              </label>

              <label className="block">
                <span className="text-gray-700">Location of Hazard:</span>
                <input type="text" name="location" value={formData.location} onChange={handleChange}
                  className="mt-1 block w-full border border-gray-300 rounded-md p-2" />
              </label>

              <label className="block">
                <span className="text-gray-700">Describe the Hazard:</span>
                <select name="hazard" value={formData.hazard} onChange={handleChange}
                  className="mt-1 block w-full border border-gray-300 rounded-md p-2">
                  <option value="">-- Select an Option --</option>
                  {hazardOptions.map((option, index) => (
                    <option key={index} value={option}>{option}</option>
                  ))}
                </select>
              </label>

              <label className="block">
                <span className="text-gray-700">Has anyone been hurt?</span>
                <div className="mt-2 flex gap-4">
                  <label>
                    <input type="radio" name="hurt" value="Yes" checked={formData.hurt === "Yes"} onChange={handleChange} /> Yes
                  </label>
                  <label>
                    <input type="radio" name="hurt" value="No" checked={formData.hurt === "No"} onChange={handleChange} /> No
                  </label>
                </div>
              </label>

              {formData.hurt === "Yes" && (
                <label className="block">
                  <span className="text-gray-700">If yes, who?</span>
                  <input type="text" name="hurtDetails" value={formData.hurtDetails} onChange={handleChange}
                    className="mt-1 block w-full border border-gray-300 rounded-md p-2" />
                </label>
              )}

              <label className="block">
                <span className="text-gray-700">Action Taken (if any):</span>
                <textarea name="actionTaken" value={formData.actionTaken} onChange={handleChange}
                  className="mt-1 block w-full border border-gray-300 rounded-md p-2" rows={4} />
              </label>

              <button type="submit"
                className="mt-4 bg-indigo-600 text-white font-semibold px-6 py-2 rounded hover:text-white border-primary">
                Submit Report
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}
