export function PublicLayout({ children }) {
  const [open, setOpen] = React.useState(false);

  return (
    <>
      {/* Full-width sticky header with shadow */}
      <header className="sticky top-0 z-50 bg-white shadow-md w-full">
        <div className="max-w-7xl mx-auto flex justify-between items-center py-8 px-4">

          {/* Logo */}
          <div className="text-2xl flex items-center gap-2 font-bold uppercase">
            <Link to="/" className="navbar-logo text-primary">Compliance-App</Link>
          </div>

          {/* Desktop Navigation Links */}
          <nav className="hidden md:block">
            <ul className="flex items-center gap-6 text-gray-600">
              {HomeMenu.map((item) => (
                <li key={item.id}>
                  <a
                    href={item.link}
                    className="inline-block py-1 px-3 hover:text-primary font-semibold"
                  >
                    {item.title}
                  </a>
                </li>
              ))}
            </ul>
          </nav>

          {/* Action Buttons */}
          <div className="hidden md:flex items-center gap-4">
            <button className="border-2 border-primary text-primary px-6 py-2 rounded-md hover:bg-primary hover:text-white transition font-semibold">
              Login
            </button>
            <button className="bg-primary hover:bg-primary/90 text-white px-6 py-2 rounded-md border-2 border-primary transition font-semibold">
              Sign up for free
            </button>
          </div>

          {/* Mobile Hamburger Icon */}
          <div className="md:hidden" onClick={() => setOpen(!open)}>
            <span className="text-4xl text-gray-700">&#9776;</span>
          </div>
        </div>

        {/* Mobile Overlay Menu */}
        <ResponsiveMenu open={open} />
      </header>

      {/* Page Content */}
      <main className="max-w-6xl mx-auto px-4 md:px-8 py-6">
        {children}
      </main>
    </>
  );
}
