import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

// Accordion wrapper
function AccordionSection({ title, children }) {
  const [open, setOpen] = useState(true);
  return (
    <div id={title} className="mb-6 border rounded shadow-sm">
      <button
        onClick={() => setOpen(!open)}
        className="w-full text-left px-4 py-3 bg-gray-100 hover:bg-gray-200 font-semibold"
      >
        {title}
      </button>
      {open && <div className="p-4 bg-white">{children}</div>}
    </div>
  );
}

// Checklist table
function ChecklistSection({ items }) {
  return (
    <table className="w-full border border-gray-300 mb-4">
      <thead className="bg-gray-100">
        <tr>
          <th className="p-2 border">Item</th>
          <th className="p-2 border">Status</th>
          <th className="p-2 border">Comments</th>
        </tr>
      </thead>
      <tbody>
        {items.map((item, idx) => (
          <tr key={idx}>
            <td className="p-2 border">{item}</td>
            <td className="p-2 border">
              <select className="border rounded p-1 w-full">
                <option>√</option>
                <option>⤫</option>
                <option>N/A</option>
              </select>
            </td>
            <td className="p-2 border">
              <input type="text" className="border rounded p-1 w-full" placeholder="Comment" />
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

// Issues & Action Plan table
function IssuesActionPlanTable() {
  return (
    <table className="w-full border border-gray-300 mb-6">
      <thead className="bg-gray-100">
        <tr>
          <th className="p-2 border">Issue</th>
          <th className="p-2 border">Corrective Action</th>
          <th className="p-2 border">Person Responsible</th>
          <th className="p-2 border">Due Date</th>
        </tr>
      </thead>
      <tbody>
        {[...Array(3)].map((_, idx) => (
          <tr key={idx}>
            <td className="p-2 border"><input type="text" className="w-full border p-1" /></td>
            <td className="p-2 border"><input type="text" className="w-full border p-1" /></td>
            <td className="p-2 border"><input type="text" className="w-full border p-1" /></td>
            <td className="p-2 border"><input type="date" className="w-full border p-1" /></td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

// Section navigation
function SectionNav({ sections }) {
  const scrollToSection = (id) => {
    const el = document.getElementById(id);
    if (el) el.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <div className="space-y-2">
      {sections.map((section, idx) => (
        <button
          key={idx}
          className="block w-full text-left px-4 py-2 bg-gray-100 hover:bg-gray-200 rounded"
          onClick={() => scrollToSection(section)}
        >
          {idx + 1}. {section}
        </button>
      ))}
    </div>
  );
}

// Main page
export default function CanteenFormPage() {
  const navigate = useNavigate();
  const sections = [
    "General Information",
    "Kitchen Area",
    "Serving Area",
    "Eating Area",
    "Personnel & Procedures",
    "Issues & Action Plan",
    "Sign-Off",
  ];

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <div className="mb-6 flex justify-between items-center">
        <h1 className="text-2xl font-bold">Canteen Inspection Form</h1>
        <button
          onClick={() => navigate("/form/canteen")}
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          + Launch Canteen Form
        </button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        {/* Sidebar */}
        <div className="md:col-span-1">
          <SectionNav sections={sections} />
        </div>

        {/* Form Sections */}
        <div className="md:col-span-3 space-y-6">
          <AccordionSection title="General Information">
            <p className="text-sm text-gray-600">Form metadata, location, inspector name, date, etc.</p>
          </AccordionSection>

          <AccordionSection title="Kitchen Area">
            <ChecklistSection items={[
              "Food preparation surfaces clean",
              "Handwashing facilities available",
              "Food stored at appropriate temperatures",
            ]} />
          </AccordionSection>

          <AccordionSection title="Serving Area">
            <ChecklistSection items={[
              "Serving counters clean",
              "Utensils sanitized",
              "Protective barriers in place",
            ]} />
          </AccordionSection>

          <AccordionSection title="Eating Area">
            <ChecklistSection items={[
              "Tables and chairs clean",
              "Waste bins available",
              "Ventilation adequate",
            ]} />
          </AccordionSection>

          <AccordionSection title="Personnel & Procedures">
            <ChecklistSection items={[
              "Staff wearing appropriate attire",
              "Hand hygiene observed",
              "Training records available",
            ]} />
          </AccordionSection>

          <AccordionSection title="Issues & Action Plan">
            <IssuesActionPlanTable />
          </AccordionSection>

          <AccordionSection title="Sign-Off">
            <p className="text-sm text-gray-600">Signature fields, final comments, and submission button.</p>
          </AccordionSection>
        </div>
      </div>
    </div>
  );
}
