import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

// Accordion wrapper
function AccordionSection({ title, children }) {
  const [open, setOpen] = useState(true);
  return (
    <div id={title} className="mb-6 border rounded shadow-sm">
      <button
        onClick={() => setOpen(!open)}
        className="w-full text-left px-4 py-3 bg-gray-100 hover:bg-gray-200 font-semibold"
      >
        {title}
      </button>
      {open && <div className="p-4 bg-white">{children}</div>}
    </div>
  );
}

// Checklist table
function ChecklistSection({ items }) {
  return (
    <table className="w-full border border-gray-300 mb-4">
      <thead className="bg-gray-100">
        <tr>
          <th className="p-2 border">Item</th>
          <th className="p-2 border">Status</th>
          <th className="p-2 border">Comments</th>
        </tr>
      </thead>
      <tbody>
        {items.map((item, idx) => (
          <tr key={idx}>
            <td className="p-2 border">{item}</td>
            <td className="p-2 border">
              <select className="border rounded p-1 w-full">
                <option>√</option>
                <option>⤫</option>
                <option>N/A</option>
              </select>
            </td>
            <td className="p-2 border">
              <input type="text" className="border rounded p-1 w-full" placeholder="Comment" />
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

// Stepper for Category Information
function CategoryStepper() {
  const steps = [
    {
      title: "Kitchen Area",
      items: [
        "Surfaces clean",
        "Handwashing available",
        "Food temp OK",
        "Waste bins covered",
        "No signs of pests"
      ]
    },
    {
      title: "Serving Area",
      items: [
        "Counters clean",
        "Utensils sanitized",
        "Hot food held properly",
        "Cold food refrigerated",
        "Allergen info posted"
      ]
    },
    {
      title: "Eating Area",
      items: [
        "Tables clean",
        "Bins available",
        "Floors swept",
        "Ventilation adequate",
        "Hand sanitizer nearby"
      ]
    },
    {
      title: "Personnel & Procedures",
      items: [
        "Staff attire",
        "Hand hygiene",
        "Training records",
        "First aid kit available",
        "Emergency numbers posted"
      ]
    }
  ];

  const [currentStep, setCurrentStep] = useState(0);
  const progressPercent = ((currentStep + 1) / steps.length) * 100;

  return (
    <div className="space-y-6">
      {/* Progress Bar */}
      <div className="w-full bg-gray-200 rounded-full h-3">
        <div
          className="bg-green-600 h-3 rounded-full transition-all duration-300"
          style={{ width: `${progressPercent}%` }}
        />
      </div>

      {/* Step Title + Navigation */}
      <div className="flex justify-between items-center">
        <h3 className="text-lg font-semibold text-gray-800">{steps[currentStep].title}</h3>
        <div className="space-x-2">
          <button
            disabled={currentStep === 0}
            onClick={() => setCurrentStep(currentStep - 1)}
            className="px-3 py-1 bg-gray-200 rounded disabled:opacity-50"
          >
            Previous
          </button>
          <button
            disabled={currentStep === steps.length - 1}
            onClick={() => setCurrentStep(currentStep + 1)}
            className="px-3 py-1 bg-green-600 text-white rounded disabled:opacity-50"
          >
            Next
          </button>
        </div>
      </div>

      {/* Checklist Table */}
      <ChecklistSection items={steps[currentStep].items} />
    </div>
  );
}

// Issues & Action Plan table
function IssuesActionPlanTable() {
  return (
    <table className="w-full border border-gray-300 mb-6">
      <thead className="bg-gray-100">
        <tr>
          <th className="p-2 border">Issue</th>
          <th className="p-2 border">Corrective Action</th>
          <th className="p-2 border">Person Responsible</th>
          <th className="p-2 border">Due Date</th>
        </tr>
      </thead>
      <tbody>
        {[...Array(3)].map((_, idx) => (
          <tr key={idx}>
            <td className="p-2 border"><input type="text" className="w-full border p-1" /></td>
            <td className="p-2 border"><input type="text" className="w-full border p-1" /></td>
            <td className="p-2 border"><input type="text" className="w-full border p-1" /></td>
            <td className="p-2 border"><input type="date" className="w-full border p-1" /></td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

// Section navigation
function SectionNav({ sections }) {
  const scrollToSection = (id) => {
    const el = document.getElementById(id);
    if (el) el.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <div className="space-y-2">
      {sections.map((section, idx) => (
        <button
          key={idx}
          className="block w-full text-left px-4 py-2 bg-gray-100 hover:bg-gray-200 rounded"
          onClick={() => scrollToSection(section)}
        >
          {idx + 1}. {section}
        </button>
      ))}
    </div>
  );
}

// Main page
export default function CanteenFormPage() {
  const navigate = useNavigate();
  const sections = [
    "General Information",
    "Category Information",
    "Issues & Action Plan",
    "Sign-Off"
  ];

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <div className="mb-6 flex justify-between items-center">
        <h1 className="text-2xl font-bold">Canteen Inspection Form</h1>
        <button
          onClick={() => navigate("/form/canteen")}
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
