import './index.css';
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Sidebar } from './components/Sidebar';
import { Dashboard } from './components/Dashboard';
import { Layout } from './components/Layout';
import { Home } from './components/Home/Home';
import { HazardReportForm } from './components/Dashboard/HazardRiskManagements';
import { RiskAssessmentForm}  from './components/Dashboard/HealthAndSafety';
import IncidentInvestigationForm from './components/Dashboard/IncidentsInvestigationForm';


export default function App() {
  const [sidebarToggle, setSidebarToggle] = useState(false);

  return (
    <Router>
      <Routes>
        {/* Home page with no sidebar */}
        <Route path="/" element={<Home />} />

        {/* Dashboard page with sidebar */}
        <Route
          path="/dashboard"
          element={
            <div className="flex">
              <Sidebar sidebarToggle={sidebarToggle} />
              <Dashboard
                sidebarToggle={sidebarToggle}
                setSidebarToggle={setSidebarToggle}
              />
            </div>
          }
        />




<Route
  path="/hazard"
  element={
    <Layout
      sidebarToggle={sidebarToggle}
      setSidebarToggle={setSidebarToggle}
    >
      <HazardReportForm />
    </Layout>
  }
/>

<Route
  path="/health-and-safety"
  element={
    <Layout
      sidebarToggle={sidebarToggle}
      setSidebarToggle={setSidebarToggle}
    >
      <RiskAssessmentForm />
    </Layout>
  }
/>

<Route
  path="/incident-management"
  element={
    <Layout
      sidebarToggle={sidebarToggle}
      setSidebarToggle={setSidebarToggle}
    >
      <IncidentInvestigationForm />
    </Layout>
  }
/>









      </Routes>
    </Router>
  );
}
