import React, { useEffect, useState } from 'react'
import { createContext } from 'react'
import { ChevronDown } from 'lucide-react'
import { useContext } from 'react'


const AccordianContext = createContext()


export default function CanteenForm({children,value,onChange,...props}) {
  const [selected,setSelected]= useState(value)
  useEffect(()=>{
    onChange?.( selected )
  }, [selected, onChange])
  return (
    <ul {...props}>
      <AccordianContext.Provider value={{selected, setSelected}}>
        {children}

      </AccordianContext.Provider>

    </ul>
  )
}

export function AccordianItem({children,value,trigger,...props}){
  const {selected, setSelected} = useContext(AccordianContext)
  const open = selected === value

  return(
    <li className='border-b' {...props}>
      <header role='button' onClick={() => setSelected(open? null : value)}
        className={'flex justify-between items-center py-4 font-medium'}>
        {trigger}
        <ChevronDown size={16}/>
      </header>
      <div className='overflow-y-hidden' style={{ height: open ? '100%' : 0 }}>
        <div className='p-2 pb-4'>{children}</div>
      </div>

    </li>
  )
}
