import React, { useState } from 'react';

const AccordionSection = ({ title, children }) => {
  const [open, setOpen] = useState(false);
  return (
    <div className="border rounded mb-4 shadow">
      <button
        onClick={() => setOpen(!open)}
        className="w-full text-left px-4 py-3 bg-gray-100 hover:bg-gray-200 font-semibold"
      >
        {title}
      </button>
      {open && <div className="p-4">{children}</div>}
    </div>
  );
};

const ChecklistTable = ({ title, items }) => (
  <div className="mb-6">
    <h3 className="text-lg font-semibold mb-2">{title}</h3>
    <table className="w-full table-auto border border-gray-300">
      <thead className="bg-gray-200">
        <tr>
          <th className="p-2 border">Item</th>
          <th className="p-2 border">Status</th>
          <th className="p-2 border">Comments</th>
        </tr>
      </thead>
      <tbody>
        {items.map((item, idx) => (
          <tr key={idx}>
            <td className="p-2 border">{item}</td>
            <td className="p-2 border">
              <select className="border rounded p-1 w-full">
                <option>√</option>
                <option>⤫</option>
                <option>N/A</option>
              </select>
            </td>
            <td className="p-2 border">
              <input type="text" className="border rounded p-1 w-full" />
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  </div>
);

const CanteenForm = () => {
  const [issues, setIssues] = useState([]);

  const handleAddIssue = () => {
    setIssues([...issues, { issue: '', action: '', person: '', date: '' }]);
  };

  const handleChange = (index, field, value) => {
    const updated = [...issues];
    updated[index][field] = value;
    setIssues(updated);
  };

  return (
    <div className="max-w-6xl mx-auto p-6">
      <h1 className="text-2xl font-bold mb-6">Canteen Inspection Form</h1>

      {/* Section 1: General Info */}
      <AccordionSection title="Section 1: General Information">
        <div className="grid grid-cols-2 gap-4">
          {['School Name', 'Location', 'Date of Inspection', 'Time', 'Inspected By', 'Position/Title'].map((label, idx) => (
            <input key={idx} type="text" placeholder={label} className="border p-2 rounded w-full" />
          ))}
        </div>
      </AccordionSection>

      {/* Section 2: Inspection Checklist */}
      <AccordionSection title="Section 2: Inspection Checklist">
        <ChecklistTable
          title="Kitchen Area"
          items={[
            'Food preparation surfaces clean and sanitized.',
            'Handwashing facilities available and functional.',
            'Food stored at appropriate temperatures.',
            'Perishables stored off the floor and covered.',
            'Waste bins covered and emptied regularly.',
            'Pest control measures in place and effective.',
            'Refrigerators and freezers functioning properly.',
            'Cooking equipment is clean and maintained.',
            'Cleaning supplies stored separately.',
            'Floor clean, dry, and slip-resistant.',
            'No signs of pests.'
          ]}
        />
        <ChecklistTable
          title="Serving Area"
          items={[
            'Serving counters clean and clutter-free.',
            'Food handlers wear gloves/hairnets/aprons.',
            'Hot food held at proper temperature.',
            'Cold food kept refrigerated or on ice.',
            'Utensils and serving equipment clean.',
            'Displayed food covered and protected.',
            'Allergen information visibly posted.'
          ]}
        />
        <ChecklistTable
          title="Eating Area"
          items={[
            'Tables and chairs clean and undamaged.',
            'Waste bins available and emptied frequently.',
            'Floors swept/mopped regularly.',
            'Adequate ventilation and lighting.',
            'No signs of pests.',
            'Hand sanitizer or wash station nearby.'
          ]}
        />
      </AccordionSection>

      {/* Section 3: Personnel & Procedures */}
      <AccordionSection title="Section 3: Personnel & Procedures">
        <ChecklistTable
          title="Personnel & Procedures"
          items={[
            'Canteen staff trained in food safety.',
            'Staff wear clean uniforms/PPE.',
            'Cleaning schedules followed and documented.',
            'First aid kit available in canteen.',
            'Emergency numbers posted in visible area.'
          ]}
        />
      </AccordionSection>

      {/* Section 4: Issues & Action Plan */}
      <AccordionSection title="Section 4: Issues & Action Plan">
        <button
          className="mb-4 px-4 py-2 bg-blue-600 text-white rounded"
          onClick={handleAddIssue}
        >
          + Create Issue
        </button>
        <table className="w-full table-auto border border-gray-300">
          <thead className="bg-gray-200">
            <tr>
              <th className="p-2 border">Issue</th>
              <th className="p-2 border">Corrective Action</th>
              <th className="p-2 border">Person Responsible</th>
              <th className="p-2 border">Due Date</th>
            </tr>
          </thead>
          <tbody>
            {issues.map((issue, idx) => (
              <tr key={idx}>
                <td className="p-2 border">
                  <input
                    type="text"
                    value={issue.issue}
                    onChange={(e) => handleChange(idx, 'issue', e.target.value)}
                    className="border rounded p-1 w-full"
                  />
                </td>
                <td className="p-2 border">
                  <input
                    type="text"
                    value={issue.action}
                    onChange={(e) => handleChange(idx, 'action', e.target.value)}
                    className="border rounded p-1 w-full"
                  />
                </td>
                <td className="p-2 border">
                  <input
                    type="text"
                    value={issue.person}
                    onChange={(e) => handleChange(idx, 'person', e.target.value)}
                    className="border rounded p-1 w-full"
                  />
                </td>
                <td className="p-2 border">
                  <input
                    type="date"
                    value={issue.date}
                    onChange={(e) => handleChange(idx, 'date', e.target.value)}
                    className="border rounded p-1 w-full"
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </AccordionSection>
    </div>
  );
};

export default CanteenForm;
