import React from "react";
import { ResponsiveMenu } from "./Home/ResponsiveMenu";
import { HomeMenu } from "../mockData/data";
import { Link } from "react-router-dom";

export function PublicLayout({ children }) {
  const [open, setOpen] = React.useState(false);

  return (
    <>
      <header className="container flex justify-between items-center py-8">
        <div className="text-2xl flex items-center gap-2 font-bold uppercase">
          <p>Compliance</p>
          <p className="text-secondary">App</p>
        </div>

        <nav className="hidden md:block">
          <ul className="flex items-center gap-6 text-gray-600">
            {HomeMenu.map((item) => (
              <li key={item.id}>
                <Link to={item.link} className="inline-block py-1 px-3 hover:text-primary font-semibold">
                  {item.title}
                </Link>
              </li>
            ))}
          </ul>
        </nav>

        <div className="flex items-center gap-4">
          <button className="hover:bg-primary text-primary font-semibold border-2 border-primary px-6 py-2 rounded-md duration-200 hidden md:block">
            Login
          </button>
          <button className="bg-primary hover:bg-primary text-white font-semibold border-2 border-primary py-2 px-6 rounded duration-200 hidden md:block">
            Sign up for free
          </button>
        </div>

        <div className="md:hidden" onClick={() => setOpen(!open)}>
          <span className="text-4xl">&#9776;</span>
        </div>
      </header>

      <ResponsiveMenu open={open} />

      <main className="max-w-6xl mx-auto px-4 md:px-8 py-6">{children}</main>
    </>
  );
}
