import React from 'react';
import { AboutUs } from '../Pages/AboutUs';
import { Products } from '../Pages/Products';
import { Solutions } from '../Pages/Solutions';
import { Contacts } from '../Pages/Contacts';

export function Home() {
  return (
    <main className="scroll-smooth">
     {/*<section id="hero" className="min-h-screen flex items-center justify-center bg-blue-100">
        <h1 className="text-4xl font-bold">Welcome to the Compliance App! 🌟</h1>
      </section>*/}


      <section
  className="min-h-screen bg-cover bg-center flex flex-col items-center justify-center px-6 py-12"
  style={{ backgroundImage: "url('/image.png')" }} // 🔁 Replace with your image path
>
  {/* Hero content overlay */}
  <div className="bg-white bg-opacity-90 rounded-md p-8 text-center max-w-3xl shadow-md">
    <h1 className="text-4xl font-bold text-indigo-800 mb-6">
      Build a Safer, Smarter Workplace
    </h1>

    <div className="flex gap-4 justify-center mb-6">
      <button className="px-6 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700 transition">
        Sign In
      </button>
      <button className="px-6 py-2 bg-white text-indigo-600 border border-indigo-600 rounded hover:bg-indigo-50 transition">
        Book a Demo
      </button>
    </div>

    {/* Feature Icons Section */}
    <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-6 mt-6">
      {[
        "Mobile Inspection",
        "Incident Reporting",
        "Compliance & Document Control",
        "Dashboard & Analytics",
        "Custom Forms & Checklists"
      ].map((label, idx) => (
        <div key={idx} className="text-center">
          {/* Replace with your actual icons below */}
          <div className="bg-indigo-100 p-4 rounded-full mx-auto mb-2 w-12 h-12 flex items-center justify-center text-indigo-600 font-bold text-xl">
            {label[0]}
          </div>
          <p className="text-sm font-semibold text-gray-700">{label}</p>
        </div>
      ))}
    </div>
  </div>
</section>










      <section id="aboutus"><AboutUs /></section>
      <section id="products"><Products /></section>
      <section id="solutions"><Solutions /></section>
      <section id="contacts"><Contacts /></section>
    </main>
  );
}
